    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
          try
{
                        Connection con=(Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/db_projectorganization","root","");
			Statement st=con.createStatement();
                        ResultSet rs=st.executeQuery("select username,link_mob_no from prime_membership");
                        int f=0;
                        while(rs.next())
                        {
                            String user=rs.getString(1);
                            System.out.println(user);
                            String p=rs.getString(2);
                            String tun=usern.getText();
                            String tps=lmno.getText();
                            if(user.equals(tun) && p.equals(tps))
                            {
                            f=1;
                            break;
                            
                            }
                            else
                            {
                            f=0;
                            }
                            
                        }
                        if(f==1)
                        {
                        JOptionPane.showConfirmDialog(null," You are prime member ");
                                    jTextArea1.setVisible(true);
         prime.setVisible(false);
        ppt.setVisible(false);
        design.setVisible(false);
         prime_confirmation.setVisible(false);
        about.setVisible(false);            
                        
                        }
                        else
                        {
                        JOptionPane.showConfirmDialog(null,"You are not prime member ");
                        System.exit(0);
                        }
                        con.close();
}
          catch(SQLException ex)
          {
          System.out.println(ex);
          }
         

         
    }//GEN-LAST:event_jButton3ActionPerformed

