    private void sourcesMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_sourcesMouseClicked
        // TODO add your handling code here:

        int confirmed = JOptionPane.showConfirmDialog(null, "Are You Prime Member Of Project Organisation Software...?","Confirmation",JOptionPane.YES_NO_OPTION);

        if(confirmed == JOptionPane.NO_OPTION)
        {
            prime.setVisible(true);
            ppt.setVisible(false);
            design.setVisible(false);
            about.setVisible(false);
            jTextArea1.setVisible(false);

        }
        else
        {
            prime_confirmation.setVisible(true);
            prime.setVisible(false);
            ppt.setVisible(false);
            design.setVisible(false);
            about.setVisible(false);
        }
    }//GEN-LAST:event_sourcesMouseClicked

