         try
        { 
			Connection con=(Connection) DriverManager.getConnection("jdbc:mysql://localhost:3306/db_projectorganization","root","");
			Statement st=con.createStatement();
                        String i=lname.getText();
                        String ad=laddress.getText();
			String ac=laccount.getText();
			String c=lcode.getText();
                        String m=lmob.getText();
                        String p=jComboBox1.getSelectedItem().toString();
                        String pl=jComboBox2.getSelectedItem().toString();

			st.executeUpdate("insert into prime_membership values('"+i+"','"+ad+"','"+ac+"','"+c+"','"+m+"','"+p+"','"+pl+"')");
			con.close();
                        JOptionPane.showConfirmDialog(null,"Do you Want to join  a prime membership...?");
		}
