        jTextArea1.setColumns(20);
        jTextArea1.setRows(5);
        jTextArea1.setText("//*******************************************************\n//      INCLUDED HEADER FILES\n//*******************************************************\n\n#include\"stdio.h\"\n#include\"conio.h\"\n#include\"fstream.h\"\n#include\"stdlib.h\"\n#include\"dos.h\"\n#include\"string.h\"\n#include\"graphics.h\"\n#include\"iomanip.h\"\n\n//*******************************************************\n//      CLASS NAME : BOOK\n//*******************************************************\n\nclass book\n{\npublic:\n    char name[20];\n    char author[20];\n    int a;\n    void getdata();\n    void show();\n};\n\n//*******************************************************\n//      FUNCTIONS TO GET AND SHOW DATA\n//*******************************************************\n\nvoid book::getdata()\n{\n    cout< <\"\\n\\\"        \\\"ENTER DETAILS ABOUT BOOK U WANT TO PURCHASE\\\" :\\\"\";\n    cout<<\"\\n\\n\\n\\t\\tEnter Name Of Book  :\\t\";\n    cin>>name;\n    cout< <\"\\n\\t\\tEnter Name Of Author    :\\t\";\n    cin>>author;\n    fflush(stdin);\n    cout< <\"\\n\\t\\tEnter No. Of Copies  :\\t\";\n    cin>>a;\n}\nvoid book::show()\n{\n\n    cout< <\"BOOK      :\"<<name;\n    cout<<\"\\nAUTHOR  :\"<<author;\n    cout<<\"\\nCOPIES  :\"<<a;\n}\n\n//*******************************************************\n//      TO INVOKE WELCOME SCREEN, CREDIT SCREEN\n//                   & ENDING SCREEN\n//*******************************************************\n\nvoid graph()\n{\n    for(int y=0; y&lt; 440; y=y+10)\n    {\n        fillellipse(50,y,50,50);\n        setfillstyle(2,GREEN);\n        fillellipse(100,y,50,50);\n        setfillstyle(3,YELLOW);\n        fillellipse(200,y+10,50,50);\n        setfillstyle(2,WHITE);\n        fillellipse(300,y-15,50,50);\n        setfillstyle(3,6);\n        fillellipse(400,y-5,50,50);\n        setfillstyle(2,BLUE);\n        fillellipse(500,y+20,50,50);\n        setfillstyle(3,CYAN);\n        clearviewport();\n\n        for(int c=440; c&lt; 480; ++c)\n        {\n            setlinestyle(0,1,3);\n            setcolor(WHITE);\n\n            line(0,c,325,480);\n            line(680,c,325,480);\n        }\n        delay(20);\n    }\n\n    int x[5];\n    for(x[0]=50,x[1]=100,x[2]=200,x[3]=400,x[4]=500,y=450; y>=240; y=y-10,x[0]=x[0]+13,x[2]=x[2]+6,x[1]+=11,x[3]-=4,x[4]-=9)\n    {\n        fillellipse(x[0],y,20,20);\n        setfillstyle(1,GREEN);\n        fillellipse(x[1],y,20,20);\n        setfillstyle(1,YELLOW);\n        fillellipse(x[2],y+10,20,20);\n        setfillstyle(1,WHITE);\n        fillellipse(300,y-15,20,20);\n        setfillstyle(1,6);\n        fillellipse(x[3],y-5,20,20);\n        setfillstyle(1,BLUE);\n        fillellipse(x[4],y+20,20,20);\n        setfillstyle(1,CYAN);\n\n        clearviewport();\n        for(int c=440; c&lt; 480; ++c)\n        {\n            setlinestyle(0,1,3);\n            setcolor(BLACK);\n            line(0,c,325,480);\n            line(680,c,325,480);\n        }\n        delay(20);\n    }\n\n    for (int c=0; c&lt; 150; ++c)\n    {\n        setcolor(BLACK);\n        fillellipse(325,240,c*1.5,c);\n        delay(10);\n    }\n    setcolor(BLACK);\n    settextstyle(7,0,5);\n    outtextxy(115,200,\"B O O K - S H O P\");\n    getch();\n    clearviewport();\n}\n\nvoid main()\n{\n    clrscr();\n    int gdriver=DETECT,gmode;\n    initgraph(&gdriver,&gmode,\"\\\\tc\\\\bgi\");\n    graph();\n    rectangle(10,10,630,470);\n    setbkcolor(RED);\n    rectangle(10,10,630,470);\n    settextstyle(GOTHIC_FONT,0,4);\n    setcolor(WHITE);\n    outtextxy(30,35,\"Developed By:-\");\n    settextstyle(10,0,3);\n    outtextxy(150,100,\"*iCBSE.com\");\n    getch();\n    char s;\n    book b1;\nz:\n\n    fstream f;\n    f.open(\"book\",ios::in|ios::out|ios::app|ios::ate|ios::binary);\n    clearviewport();\n    settextstyle(10,HORIZ_DIR,3);\n    setbkcolor(GREEN);\n    rectangle(10,10,630,470);\n    outtextxy(100,15,\"WHAT DO U WANT TO DO:\");\n    settextstyle(SANS_SERIF_FONT,0,3);\n    outtextxy(250,100,\"1 . TO BUY\");\n    outtextxy(250,150,\"2 . TO SHOW ALL BOOKS\");\n    outtextxy(250,200,\"3 . TO CHECK AVAILABILITY\");\n    outtextxy(250,250,\"4 . TO MODIFY\");\n    outtextxy(250,300,\"5 . TO DELETE\");\n    outtextxy(250,350,\"6 . TO EXIT\");\n    int a;\n    settextstyle(7,0,5);\n    outtextxy(20,400, \"ENTER UR CHOICE:\");\n    f.seekg(0);\n    gotoxy(55,25);\n    cin>>a;\n    char x;\n    gotoxy(1,1);\n\n    switch (a)\n    {\n\n//*******************************************************\n//      CASE       : 1\n//      DETAILS    : TO ADD A BOOKï¿½S RECORD\n//*******************************************************\n\n    case 1:\n        gotoxy(1,1);\n        clearviewport();\n        rectangle(10,10,630,470);\n        setbkcolor(RED);\n        textbackground(13);\n        textcolor(6);\n        clrscr();\n        fstream f;\n        f.open(\"book\",ios::in|ios::out|ios::ate|ios::app|ios::binary);\n        char ans;\n        b1.getdata();\n        f.write((char *)&b1,sizeof(b1));\n        getch();\n        settextstyle(7,0,1);\n        outtextxy(250,410,\"Do You Want To Continue:\");\n\n        ans=getchar();\n        if(ans=='Y' || ans=='y')\n            goto z;\n        else\n            break;\n\n//*******************************************************\n//      CASE       : 2\n//      DETAILS    : TO SHOW ALL BOOKSï¿½ RECORDS\n//*******************************************************\n\n    case 2:\n    {\n        clearviewport();\n        rectangle(10,10,630,470);\n        setbkcolor(LIGHTBLUE);\n        textbackground(3);\n        textcolor(6);\n        cout< <\"\\n\\n\";\n        fstream f;\n        f.open(\"book\",ios::in|ios::out|ios::ate|ios::app|ios::binary);\n        char ans;\n        f.seekg(0);\n        int ctr=0;\n        while(f.read((char *)&b1,sizeof(b1)) )\n        {\n            ctr=ctr+1;\n            if(ctr==8)\n            {\n                getchar();\n                clrscr();\n                ctr=0;\n            }\n            b1.show();\n            if(f.eof()==1)\n            {\n                break;\n            }\n        }\n        f.close();\n        settextstyle(7,0,1);\n        outtextxy(250,410,\"Do You Want To Continue:\");\n        cin>>ans;\n        if(ans=='y'|| ans=='Y')\n            goto z;\n        else\n        {\n            closegraph();\n            exit(1);\n        }\n    }\n\n//*******************************************************\n//      CASE       : 3\n//      DETAILS    : TO CHECK AVAILABILITY\n//*******************************************************\n\n    case 3:\n    {\n        gotoxy(60,25);\n        clearviewport();\n        rectangle(10,10,630,470);\n        setbkcolor(DARKGRAY);\n        textbackground(3);\n        textcolor(5);\n        clrscr();\n        char ans;\n        ifstream f;\n        book b1;\n        char name[20];\n        char author[20];\n        int a;\n        f.open(\"book\",ios::in|ios::binary);\n        cout< <\"\\n\\n\\n          Enter book name whose record to be seen  :\";\n        cin>>name;\n        do\n        {\n            f.read((char *)&b1,sizeof(b1));\n            if(f.eof()==1)\n            {\n                break;\n            }\n            if(strcmp(b1.name,name)==0)\n            {\n                cout< <\"\\n                  Name     :\"<<b1.name;\n                cout<<\"\\n                 author   :\"<<b1.author;\n                cout<<\"\\n                 copies   :\"<<b1.a;\n                getchar();\n            }\n        }\n        while(f);\n        f.close();\n//  a:\n        settextstyle(7,0,1);\n        outtextxy(250,410,\"Do You Want To Continue:\");\n        ans=getchar();\n        if(ans=='Y'||ans=='y')\n            goto z;\n        else\n            break;\n    }\n\n//*******************************************************\n//      CASE       : 4\n//      DETAILS    : TO MODIFY A RECORD\n//*******************************************************\n\n    case 4:\n    {\n        clearviewport();\n        rectangle(10,10,630,470);\n        setbkcolor(BROWN);\n        setcolor(WHITE);\n        clrscr();\n        char ans;\n        fstream f;\n        book b1;\n        char name[20];\n        char author[20];\n        int a;\n        f.open(\"book\",ios::in|ios::binary);\n        cout<<\"\\n\";\n        cout<<\" Enter book name whose record to be changed  :\";\n        cin>>name;\n        do\n        {\n            f.read((char *)&b1,sizeof(b1));\n            if(f.eof()==1)\n            {\n                break;\n            }\n            if(strcmp(b1.name,name)==0)\n            {\n                cout< <\"\\n        Name     :\"<<b1.name;\n                cout<<\"\\n        Author   :\"<<b1.author;\n                cout<<\"\\n        Copies   :\" <<b1.a;\n                getchar();\n                cout<<\"\\n                 Enter New Values\" ;\n                cout<<\"\\n\\n      Enter the book name  :\";\n                cin>>name;\n                cout< <\"\\n        Enter author name    :\";\n                cin>>author;\n                cout< <\"\\n        Enter no. of copies  :\";\n                cin>>a;\n                strcpy(b1.name,name);\n                strcpy(b1.author,author);\n                b1.a=a;\n                int l=f.tellg();\n                f.close();\n                f.open(\"book\",ios::out|ios::binary|ios::ate);\n                f.seekg(l-sizeof(b1));\n                f.write((char *)&b1,sizeof(b1));\n            }\n        }\n        while(f);\n\n        f.close();\n        settextstyle(7,0,1);\n        outtextxy(250,410,\"Do You Want To Continue:\");\n        ans=getchar();\n        if(ans=='Y'||ans=='y')\n        {\n            goto z;\n        }\n        else\n            break;\n    }\n\n//*******************************************************\n//      CASE       : 5\n//      DETAILS    : TO DELETE A BOOKï¿½S RECORD\n//*******************************************************\n\n    case 5:\n    {\n        clearviewport();\n        rectangle(10,10,630,470);\n        setbkcolor(BROWN);\n        setcolor(WHITE);\n        char name[20];\n        f.close();\n        f.open(\"video\",ios::in|ios::ate|ios::binary);\n        settextstyle(7,0,3);\n        outtextxy(200,30,\"DELETING:-\");\n        cout< <\"\\n\\n\\n\\n\\n\\n     Enter The  Name U Want To delete:\\t\";\n        fflush(stdin);\n        cin>>name;\n        f.seekg(0);\n        int k=0;\n        do\n        {\n            f.close();\n            f.open(\"book\",ios::in|ios::ate|ios::binary);\n            f.seekg(k);\n            fflush(stdin);\n            f.read((char *)&b1,sizeof(b1));\n            if(f.eof())\n                break;\n            if(strcmp(b1.name,name)==0)\n            {\n                k+=46;\n                continue;\n            }\n            else\n            {\n                f.close();\n                f.open(\"book2\",ios::out|ios::ate|ios::binary);\n                f.write((char*)&b1, sizeof b1);\n                k+=46;\n            }\n        }\n        while(f);\n        f.close();\n        remove (\"book\");\n        rename (\"book2\",\"book\");\n        cout< <\"\\n\\n\\n\\n\\n\\tPress Any Key...\";\n        getch();\n        goto z;\n    }\n\n//*******************************************************\n//      CASE       : 6\n//      DETAILS    : TO EXIT\n//*******************************************************\n\n    case 6:\n    {\n        clearviewport();\n        rectangle(10,10,630,470);\n        setbkcolor(BROWN);\n        setcolor(BLUE);\n        settextstyle(8,0,4);\n        outtextxy(150,200,\"NOW YOU WANT TO GO\");\n        if(getch())\n        {\n            fflush(stdin);\n            clearviewport();\n            rectangle(10,10,630,470);\n            setbkcolor(LIGHTBLUE);\n            setcolor(RED);\n            window(10,10,40,11);\n            settextstyle(8,0,4);\n            outtextxy(150,200,\"\\\"THANKS FOR VISITING\\\"\");\n            getch();\n            closegraph();\n            exit(1);\n        }\n        else\n        {\n            goto z;\n        }\n    }\n    default:\n        goto z;\n    }\n}\n");
        jScrollPane1.setViewportView(jTextArea1);

        getContentPane().add(jScrollPane1, new org.netbeans.lib.awtextra.AbsoluteConstraints(300, 150, 1070, 590));

        pack();
